(61, 19, 0, '', 'Sonic Empire@|;Sonic Empire@|;Sonic Empire@|;', '@|;@|;@|;', '@|;@|;@|;', '{"\\u041a\\u0430\\u0440\\u0442\\u0438\\u043d\\u043a\\u0430 \\u043a\\u043b\\u0456\\u0454\\u043d\\u0442\\u0430":"upload\\/articles\\/61\\/img\\/61-59522333da822.png"}', '', '[]', '', 6, '0000-00-00 00:00:00', '@|;@|;@|;', '@|;@|;@|;', '@|;@|;@|;', 1, '2017-06-22 14:16:30', '2017-06-27 10:10:21'),
(62, 19, 0, '', 'Stalker@|;Stalker@|;Stalker@|;', '@|;@|;@|;', '@|;@|;@|;', '{"\\u041a\\u0430\\u0440\\u0442\\u0438\\u043d\\u043a\\u0430 \\u043a\\u043b\\u0456\\u0454\\u043d\\u0442\\u0430":"upload\\/articles\\/62\\/img\\/62-595225dce0875.png"}', '', '[]', '', 8, '0000-00-00 00:00:00', '@|;@|;@|;', '@|;@|;@|;', '@|;@|;@|;', 1, '2017-06-22 14:16:54', '2017-06-27 10:09:21'),
(63, 19, 0, '', 'ДЕНЬ Project@|;ДЕНЬ Project@|;ДЕНЬ Project@|;', '@|;@|;@|;', '@|;@|;@|;', '{"\\u041a\\u0430\\u0440\\u0442\\u0438\\u043d\\u043a\\u0430 \\u043a\\u043b\\u0456\\u0454\\u043d\\u0442\\u0430":"upload\\/articles\\/63\\/img\\/63-5952297574d62.png"}', '', '[]', '', 7, '0000-00-00 00:00:00', '@|;@|;@|;', '@|;@|;@|;', '@|;@|;@|;', 1, '2017-06-22 14:17:20', '2017-06-27 10:09:29'),
(64, 19, 0, '', 'Дорога додому@|;Дорога домой@|;The Way Home@|;', '@|;@|;@|;', '@|;@|;@|;', '{"\\u041a\\u0430\\u0440\\u0442\\u0438\\u043d\\u043a\\u0430 \\u043a\\u043b\\u0456\\u0454\\u043d\\u0442\\u0430":"upload\\/articles\\/64\\/img\\/64-59522a12309a4.png"}', '', '[]', '', 9, '0000-00-00 00:00:00', '@|;@|;@|;', '@|;@|;@|;', '@|;@|;@|;', 1, '2017-06-22 14:18:03', '2017-06-27 10:08:59'),
